console.info("[mobile-scrollbar.js] Version - 1.0");class ArticleScrollbar{constructor(mobile=!0,heightActive="4px",heightContainer="4px",colorActive="#f23232",colorContainer="#f2f2f2"){this.settings={height:{active:heightActive,container:heightContainer},color:{active:colorActive,container:colorContainer},mobile:mobile};this.Create(this.settings)}
Create(settings)
{let container=document.createElement("div");container.id="mobile-scrollbar-container";let active=document.createElement("div");active.id="mobile-scrollbar-active";container.style.position="fixed";container.style.top=0;container.style.width="100%";container.style.zIndex="1000000000";container.style.height=settings.height.container;container.style.backgroundColor=settings.color.container;active.style.position="fixed";active.style.top=0;active.style.height=settings.height.active;active.style.backgroundColor=settings.color.active;let that=this;if(settings.mobile)
{if(/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)){container.style.display="block";window.addEventListener("scroll",function(){that.EventScroll(active)});window.addEventListener("load",function(){that.EventScroll(active);container.append(active);document.body.append(container)})}
else{container.style.display="none"}}
else{window.addEventListener("scroll",function(){that.EventScroll(active)});window.addEventListener("load",function(){that.EventScroll(active);container.append(active);document.body.append(container)})}
return!0}
EventScroll(active=document.getElementById("mobile-scrollbar-activity"))
{active.style.width=this.GetScrollPercent()+"%";return!0}
GetScrollPercent()
{let h=document.documentElement;let b=document.body;return(h.scrollTop||b.scrollTop)/((h.scrollHeight||b.scrollHeight)-h.clientHeight)*100}
Destroy()
{document.getElementById("mobile-scrollbar-container").remove();document.getElementById("mobile-scrollbar-active").remove();return!0}}